mapper.select:
SELECT COUNT(*)
FROM clothes
WHERE cloth_type = #{category}

SELECT cloth_id, cloth_name, cloth_type, cloth_description, cloth_price, cloth_discount, cloth_size, cloth_defaultImage FROM clothes WHERE cloth_type = #{category}
ORDER BY cloth_price ASC
ORDER BY cloth_price DESC

SELECT COUNT(*)
FROM shoppingcart
WHERE user_seq = #{userSeq}
